package com.home.commonGame.net.request.login;
import com.home.commonBase.data.login.ClientLoginServerInfoData;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 切换game服消息(generated by shine) */
public class SwitchGameRequest extends GameRequest
{
	/** 服务器目标 */
	public ClientLoginServerInfoData info;
	
	/** 数据类型ID */
	public static final int dataID=GameRequestType.SwitchGame;
	
	public SwitchGameRequest()
	{
		_dataID=GameRequestType.SwitchGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		ClientLoginServerInfoData infoTemp=info;
		if(infoTemp!=null)
		{
			this.info=(ClientLoginServerInfoData)infoTemp.clone();
		}
		else
		{
			this.info=null;
			nullObjError("info");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SwitchGameRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("info");
		writer.sb.append(':');
		if(this.info!=null)
		{
			this.info.writeDataString(writer);
		}
		else
		{
			writer.sb.append("ClientLoginServerInfoData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.info!=null)
		{
			stream.writeDataFullNotNull(this.info);
		}
		else
		{
			nullObjError("info");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.info!=null)
		{
			stream.writeDataSimpleNotNull(this.info);
		}
		else
		{
			nullObjError("info");
		}
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.info.release(pool);
		this.info=null;
	}
	
	/** 创建实例 */
	public static SwitchGameRequest create(ClientLoginServerInfoData info)
	{
		SwitchGameRequest re=(SwitchGameRequest)BytesControl.createRequest(dataID);
		re.info=info;
		return re;
	}
	
}
