package com.home.commonGame.net.request.mail;
import com.home.commonBase.data.mail.MailData;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.collection.SList;
import com.home.shine.support.pool.DataPool;

/** 回复获取全部邮件消息(generated by shine) */
public class ReGetAllMailRequest extends GameRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.ReGetAllMail;
	
	/** 数据 */
	public SList<MailData> list;
	
	public ReGetAllMailRequest()
	{
		_dataID=GameRequestType.ReGetAllMail;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		SList<MailData> listTemp=list;
		if(listTemp!=null)
		{
			this.list=new SList<MailData>(MailData[]::new,listTemp.size());
			SList<MailData> listT=this.list;
			if(!listTemp.isEmpty())
			{
				Object[] listVValues=listTemp.getValues();
				for(int listVI=0,listVLen=listTemp.length();listVI<listVLen;++listVI)
				{
					MailData listV=(MailData)listVValues[listVI];
					MailData listU;
					if(listV!=null)
					{
						listU=(MailData)listV.clone();
					}
					else
					{
						listU=null;
						nullObjError("listU");
					}
					
					listT.add(listU);
				}
			}
		}
		else
		{
			this.list=null;
			nullObjError("list");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ReGetAllMailRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("list");
		writer.sb.append(':');
		writer.sb.append("List<MailData>");
		if(this.list!=null)
		{
			SList<MailData> listT=this.list;
			int listLen=listT.size();
			writer.sb.append('(');
			writer.sb.append(listLen);
			writer.sb.append(')');
			writer.writeEnter();
			writer.writeLeftBrace();
			for(int listI=0;listI<listLen;++listI)
			{
				MailData listV=listT.get(listI);
				writer.writeTabs();
				writer.sb.append(listI);
				writer.sb.append(':');
				if(listV!=null)
				{
					listV.writeDataString(writer);
				}
				else
				{
					writer.sb.append("MailData=null");
				}
				
				writer.writeEnter();
			}
			writer.writeRightBrace();
		}
		else
		{
			writer.sb.append("=null");
		}
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.list!=null)
		{
			stream.writeLen(this.list.size());
			if(!this.list.isEmpty())
			{
				Object[] listVValues=this.list.getValues();
				for(int listVI=0,listVLen=this.list.length();listVI<listVLen;++listVI)
				{
					MailData listV=(MailData)listVValues[listVI];
					if(listV!=null)
					{
						stream.writeDataFullNotNull(listV);
					}
					else
					{
						nullObjError("listV");
					}
					
				}
			}
		}
		else
		{
			nullObjError("list");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.list!=null)
		{
			stream.writeLen(this.list.size());
			if(!this.list.isEmpty())
			{
				Object[] listVValues=this.list.getValues();
				for(int listVI=0,listVLen=this.list.length();listVI<listVLen;++listVI)
				{
					MailData listV=(MailData)listVValues[listVI];
					if(listV!=null)
					{
						stream.writeDataSimpleNotNull(listV);
					}
					else
					{
						nullObjError("listV");
					}
					
				}
			}
		}
		else
		{
			nullObjError("list");
		}
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		if(this.list!=null)
		{
			if(!this.list.isEmpty())
			{
				Object[] listVValues=this.list.getValues();
				for(int listVI=0,listVLen=this.list.length();listVI<listVLen;++listVI)
				{
					MailData listV=(MailData)listVValues[listVI];
					listV.release(pool);
					listV=null;
					listVValues[listVI]=null;
				}
			}
			this.list.justClearSize();
		}
	}
	
	/** 创建实例 */
	public static ReGetAllMailRequest create(SList<MailData> list)
	{
		ReGetAllMailRequest re=(ReGetAllMailRequest)BytesControl.createRequest(dataID);
		re.list=list;
		return re;
	}
	
}
