package com.home.commonGame.net.request.role;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 刷新货币消息(generated by shine) */
public class RefreshCurrencyRequest extends GameRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.RefreshCurrency;
	
	/** 类型 */
	public int type;
	
	/** 值 */
	public long value;
	
	public RefreshCurrencyRequest()
	{
		_dataID=GameRequestType.RefreshCurrency;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "RefreshCurrencyRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("type");
		writer.sb.append(':');
		writer.sb.append(this.type);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("value");
		writer.sb.append(':');
		writer.sb.append(this.value);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.type);
		
		stream.writeLong(this.value);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.type);
		
		stream.writeLong(this.value);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.type=0;
		this.value=0L;
	}
	
	/** 创建实例 */
	public static RefreshCurrencyRequest create(int type,long value)
	{
		RefreshCurrencyRequest re=(RefreshCurrencyRequest)BytesControl.createRequest(dataID);
		re.type=type;
		re.value=value;
		return re;
	}
	
}
