package com.home.commonGame.net.request.role.munit;
import com.home.commonBase.data.scene.base.SkillData;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.base.GameRequest;
import com.home.commonGame.net.request.role.munit.MUnitSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 主角加技能消息(generated by shine) */
public class MUnitAddSkillRequest extends MUnitSRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.MUnitAddSkill;
	
	/** 技能数据 */
	public SkillData skill;
	
	public MUnitAddSkillRequest()
	{
		_dataID=GameRequestType.MUnitAddSkill;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		SkillData skillTemp=skill;
		if(skillTemp!=null)
		{
			this.skill=(SkillData)skillTemp.clone();
		}
		else
		{
			this.skill=null;
			nullObjError("skill");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "MUnitAddSkillRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.skill!=null)
		{
			stream.writeDataFullNotNull(this.skill);
		}
		else
		{
			nullObjError("skill");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.skill!=null)
		{
			stream.writeDataSimpleNotNull(this.skill);
		}
		else
		{
			nullObjError("skill");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("skill");
		writer.sb.append(':');
		if(this.skill!=null)
		{
			this.skill.writeDataString(writer);
		}
		else
		{
			writer.sb.append("SkillData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.skill.release(pool);
		this.skill=null;
	}
	
	/** 创建实例 */
	public static MUnitAddSkillRequest create(int mIndex,SkillData skill)
	{
		MUnitAddSkillRequest re=(MUnitAddSkillRequest)BytesControl.createRequest(dataID);
		re.mIndex=mIndex;
		re.skill=skill;
		return re;
	}
	
}
