package com.home.commonGame.net.request.scene;
import com.home.commonBase.data.scene.scene.SceneEnterData;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 进入场景消息(依赖MessageCopy)(generated by shine) */
public class EnterSceneRequest extends GameRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.EnterScene;
	
	/** 进入数据 */
	public SceneEnterData enterData;
	
	public EnterSceneRequest()
	{
		_dataID=GameRequestType.EnterScene;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		SceneEnterData enterDataTemp=enterData;
		if(enterDataTemp!=null)
		{
			this.enterData=(SceneEnterData)enterDataTemp.clone();
		}
		else
		{
			this.enterData=null;
			nullObjError("enterData");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "EnterSceneRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("enterData");
		writer.sb.append(':');
		if(this.enterData!=null)
		{
			this.enterData.writeDataString(writer);
		}
		else
		{
			writer.sb.append("SceneEnterData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.enterData!=null)
		{
			stream.writeDataFullNotNull(this.enterData);
		}
		else
		{
			nullObjError("enterData");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.enterData!=null)
		{
			stream.writeDataSimpleNotNull(this.enterData);
		}
		else
		{
			nullObjError("enterData");
		}
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.enterData.release(pool);
		this.enterData=null;
	}
	
	/** 创建实例 */
	public static EnterSceneRequest create(SceneEnterData enterData)
	{
		EnterSceneRequest re=(EnterSceneRequest)BytesControl.createRequest(dataID);
		re.enterData=enterData;
		return re;
	}
	
}
