package com.home.commonGame.net.request.scene;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 离开场景消息(generated by shine) */
public class LeaveSceneRequest extends GameRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.LeaveScene;
	
	/** 是否还要进下一个场景 */
	public boolean hasNextScene;
	
	public LeaveSceneRequest()
	{
		_dataID=GameRequestType.LeaveScene;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "LeaveSceneRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("hasNextScene");
		writer.sb.append(':');
		writer.sb.append(this.hasNextScene);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeBoolean(this.hasNextScene);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeBoolean(this.hasNextScene);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.hasNextScene=false;
	}
	
	/** 创建实例 */
	public static LeaveSceneRequest create(boolean hasNextScene)
	{
		LeaveSceneRequest re=(LeaveSceneRequest)BytesControl.createRequest(dataID);
		re.hasNextScene=hasNextScene;
		return re;
	}
	
}
