package com.home.commonGame.net.request.scene;
import com.home.commonBase.data.scene.scene.ScenePreInfoData;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 预进入场景服务器就绪(generated by shine) */
public class PreEnterSceneNextRequest extends GameRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.PreEnterSceneNext;
	
	/** 预备信息 */
	public ScenePreInfoData preInfo;
	
	public PreEnterSceneNextRequest()
	{
		_dataID=GameRequestType.PreEnterSceneNext;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		ScenePreInfoData preInfoTemp=preInfo;
		if(preInfoTemp!=null)
		{
			this.preInfo=(ScenePreInfoData)preInfoTemp.clone();
		}
		else
		{
			this.preInfo=null;
			nullObjError("preInfo");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "PreEnterSceneNextRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("preInfo");
		writer.sb.append(':');
		if(this.preInfo!=null)
		{
			this.preInfo.writeDataString(writer);
		}
		else
		{
			writer.sb.append("ScenePreInfoData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.preInfo!=null)
		{
			stream.writeDataFullNotNull(this.preInfo);
		}
		else
		{
			nullObjError("preInfo");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.preInfo!=null)
		{
			stream.writeDataSimpleNotNull(this.preInfo);
		}
		else
		{
			nullObjError("preInfo");
		}
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.preInfo.release(pool);
		this.preInfo=null;
	}
	
	/** 创建实例 */
	public static PreEnterSceneNextRequest create(ScenePreInfoData preInfo)
	{
		PreEnterSceneNextRequest re=(PreEnterSceneNextRequest)BytesControl.createRequest(dataID);
		re.preInfo=preInfo;
		return re;
	}
	
}
