package com.home.commonGame.net.request.scene.scene;
import com.home.commonBase.data.scene.unit.UnitSimpleData;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.request.scene.base.SceneSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 添加绑定视野单位(generated by shine) */
public class AddBindVisionUnitRequest extends SceneSRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.AddBindVisionUnit;
	
	/** 实例ID */
	public int instanceID;
	
	/** 单位数据(如为空则为在视野中) */
	public UnitSimpleData data;
	
	public AddBindVisionUnitRequest()
	{
		_dataID=GameRequestType.AddBindVisionUnit;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		UnitSimpleData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(UnitSimpleData)dataTemp.clone();
		}
		else
		{
			this.data=null;
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "AddBindVisionUnitRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.instanceID);
		
		if(this.data!=null)
		{
			stream.writeBoolean(true);
			stream.writeDataFullNotNull(this.data);
		}
		else
		{
			stream.writeBoolean(false);
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.instanceID);
		
		if(this.data!=null)
		{
			stream.writeBoolean(true);
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			stream.writeBoolean(false);
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("instanceID");
		writer.sb.append(':');
		writer.sb.append(this.instanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("UnitSimpleData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.instanceID=0;
		if(this.data!=null)
		{
			this.data.release(pool);
			this.data=null;
		}
	}
	
	/** 创建实例 */
	public static AddBindVisionUnitRequest create(int instanceID,UnitSimpleData data)
	{
		AddBindVisionUnitRequest re=(AddBindVisionUnitRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.data=data;
		return re;
	}
	
}
