package com.home.commonGame.net.request.scene.unit;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.request.scene.base.SceneSRequest;
import com.home.commonGame.net.request.scene.base.UnitSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 单位刷新buff(generated by shine) */
public class UnitRefreshBuffRequest extends UnitSRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.UnitRefreshBuff;
	
	/** buff实例ID */
	public int buffInstanceID;
	
	/** 剩余时间 */
	public int lastTime;
	
	/** 剩余次数 */
	public int lastNum;
	
	public UnitRefreshBuffRequest()
	{
		_dataID=GameRequestType.UnitRefreshBuff;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "UnitRefreshBuffRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("buffInstanceID");
		writer.sb.append(':');
		writer.sb.append(this.buffInstanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("lastTime");
		writer.sb.append(':');
		writer.sb.append(this.lastTime);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("lastNum");
		writer.sb.append(':');
		writer.sb.append(this.lastNum);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.buffInstanceID);
		
		stream.writeInt(this.lastTime);
		
		stream.writeInt(this.lastNum);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.buffInstanceID);
		
		stream.writeInt(this.lastTime);
		
		stream.writeInt(this.lastNum);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.buffInstanceID=0;
		this.lastTime=0;
		this.lastNum=0;
	}
	
	/** 创建实例 */
	public static UnitRefreshBuffRequest create(int instanceID,int buffInstanceID,int lastTime,int lastNum)
	{
		UnitRefreshBuffRequest re=(UnitRefreshBuffRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.buffInstanceID=buffInstanceID;
		re.lastTime=lastTime;
		re.lastNum=lastNum;
		return re;
	}
	
}
