package com.home.commonGame.net.request.social;
import com.home.commonBase.data.social.base.QueryPlayerResultData;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 回复查询角色消息(generated by shine) */
public class ReQueryPlayerRequest extends GameRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.ReQueryPlayer;
	
	/** 回复角色数据 */
	public QueryPlayerResultData result;
	
	public ReQueryPlayerRequest()
	{
		_dataID=GameRequestType.ReQueryPlayer;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		QueryPlayerResultData resultTemp=result;
		if(resultTemp!=null)
		{
			this.result=(QueryPlayerResultData)resultTemp.clone();
		}
		else
		{
			this.result=null;
			nullObjError("result");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ReQueryPlayerRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.result!=null)
		{
			stream.writeDataFullNotNull(this.result);
		}
		else
		{
			nullObjError("result");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.result!=null)
		{
			stream.writeDataSimpleNotNull(this.result);
		}
		else
		{
			nullObjError("result");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("result");
		writer.sb.append(':');
		if(this.result!=null)
		{
			this.result.writeDataString(writer);
		}
		else
		{
			writer.sb.append("QueryPlayerResultData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.result.release(pool);
		this.result=null;
	}
	
	/** 创建实例 */
	public static ReQueryPlayerRequest create(QueryPlayerResultData result)
	{
		ReQueryPlayerRequest re=(ReQueryPlayerRequest)BytesControl.createRequest(dataID);
		re.result=result;
		return re;
	}
	
}
