package com.home.commonGame.net.request.social;
import com.home.commonBase.data.social.friend.ContactData;
import com.home.commonGame.constlist.generate.GameRequestType;
import com.home.commonGame.net.base.GameRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 返回搜索结果(generated by shine) */
public class ReSearchPlayerRequest extends GameRequest
{
	/** 数据类型ID */
	public static final int dataID=GameRequestType.ReSearchPlayer;
	
	/** 结果 */
	public ContactData contact;
	
	public ReSearchPlayerRequest()
	{
		_dataID=GameRequestType.ReSearchPlayer;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		ContactData contactTemp=contact;
		if(contactTemp!=null)
		{
			this.contact=(ContactData)contactTemp.clone();
		}
		else
		{
			this.contact=null;
			nullObjError("contact");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ReSearchPlayerRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("contact");
		writer.sb.append(':');
		if(this.contact!=null)
		{
			this.contact.writeDataString(writer);
		}
		else
		{
			writer.sb.append("ContactData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.contact!=null)
		{
			stream.writeDataFullNotNull(this.contact);
		}
		else
		{
			nullObjError("contact");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.contact!=null)
		{
			stream.writeDataSimpleNotNull(this.contact);
		}
		else
		{
			nullObjError("contact");
		}
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.contact.release(pool);
		this.contact=null;
	}
	
	/** 创建实例 */
	public static ReSearchPlayerRequest create(ContactData contact)
	{
		ReSearchPlayerRequest re=(ReSearchPlayerRequest)BytesControl.createRequest(dataID);
		re.contact=contact;
		return re;
	}
	
}
