package com.home.commonGame.net.response.base;
import com.home.commonGame.constlist.generate.GameResponseType;
import com.home.commonGame.net.base.GameResponse;
import com.home.commonGame.part.player.Player;
import com.home.commonGame.server.GameReceiveSocket;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.control.ThreadControl;

/** 登陆部分协议基类(generated by shine) */
public class LoginResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.Login;
	
	/** 玩家socket */
	protected GameReceiveSocket gameSocket;
	
	public LoginResponse()
	{
		_dataID=GameResponseType.Login;
		setUseMainThread();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
	
	}
	
	@Override
	public void dispatch()
	{
		gameSocket=(GameReceiveSocket)socket;
		
		Player player;
		
		if((player=gameSocket.player)!=null)
		{
			setPlayer(player);
		}
		
		//丢主线程执行
		ThreadControl.addMainFunc(this);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "LoginResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		stream.endReadObj();
	}
	
}
