package com.home.commonGame.net.response.func.aution;
import com.home.commonBase.data.item.auction.AuctionBuyItemData;
import com.home.commonGame.constlist.generate.GameResponseType;
import com.home.commonGame.net.response.func.base.FuncRResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 拍卖行购买消息(generated by shine) */
public class FuncAuctionBuyItemResponse extends FuncRResponse
{
	/** 购买物品数据 */
	public AuctionBuyItemData data;
	
	/** 数据类型ID */
	public static final int dataID=GameResponseType.FuncAuctionBuyItem;
	
	public FuncAuctionBuyItemResponse()
	{
		_dataID=GameResponseType.FuncAuctionBuyItem;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncAuctionBuyItemResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData dataT=stream.readDataFullNotNull();
		if(dataT!=null)
		{
			if(dataT instanceof AuctionBuyItemData)
			{
				this.data=(AuctionBuyItemData)dataT;
			}
			else
			{
				this.data=new AuctionBuyItemData();
				if(!(dataT.getClass().isAssignableFrom(AuctionBuyItemData.class)))
				{
					stream.throwTypeReadError(AuctionBuyItemData.class,dataT.getClass());
				}
				this.data.shadowCopy(dataT);
			}
		}
		else
		{
			this.data=null;
		}
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.data=(AuctionBuyItemData)stream.readDataSimpleNotNull();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("AuctionBuyItemData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.func.getAuctionTool(funcID).buyItem(data);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data=null;
	}
	
}
