package com.home.commonGame.net.response.func.item;
import com.home.commonGame.constlist.generate.GameResponseType;
import com.home.commonGame.net.response.func.base.FuncRResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 移动物品消息(generated by shine) */
public class FuncMoveItemResponse extends FuncRResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.FuncMoveItem;
	
	/** 从某格子 */
	public int fromIndex;
	
	/** 到某格子 */
	public int toIndex;
	
	public FuncMoveItemResponse()
	{
		_dataID=GameResponseType.FuncMoveItem;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncMoveItemResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.fromIndex=stream.readInt();
		
		this.toIndex=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.fromIndex=stream.readInt();
		
		this.toIndex=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("fromIndex");
		writer.sb.append(':');
		writer.sb.append(this.fromIndex);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("toIndex");
		writer.sb.append(':');
		writer.sb.append(this.toIndex);
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.func.getItemContainerTool(funcID).moveItem(fromIndex,toIndex);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.fromIndex=0;
		this.toIndex=0;
	}
	
}
