package com.home.commonGame.net.response.func.item;
import com.home.commonGame.constlist.generate.GameResponseType;
import com.home.commonGame.net.response.func.base.FuncRResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 脱装备(向背包)(generated by shine) */
public class FuncPutOffEquipResponse extends FuncRResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.FuncPutOffEquip;
	
	/** 装备槽位 */
	public int slot;
	
	/** 背包序号(如传-1则为自动选择) */
	public int bagIndex;
	
	public FuncPutOffEquipResponse()
	{
		_dataID=GameResponseType.FuncPutOffEquip;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncPutOffEquipResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.slot=stream.readInt();
		
		this.bagIndex=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.slot=stream.readInt();
		
		this.bagIndex=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("slot");
		writer.sb.append(':');
		writer.sb.append(this.slot);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("bagIndex");
		writer.sb.append(':');
		writer.sb.append(this.bagIndex);
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.func.getEquipContainerTool(funcID).putOffEquipToBag(slot,bagIndex);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.slot=0;
		this.bagIndex=0;
	}
	
}
