package com.home.commonGame.net.response.guide;
import com.home.commonGame.constlist.generate.GameResponseType;
import com.home.commonGame.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 设置主引导步消息(generated by shine) */
public class SetMainGuideStepResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.SetMainGuideStep;
	
	/** 主引导步 */
	public int value;
	
	public SetMainGuideStepResponse()
	{
		_dataID=GameResponseType.SetMainGuideStep;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SetMainGuideStepResponse";
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.guide.setMainStep(value,false);
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.value=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.value=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("value");
		writer.sb.append(':');
		writer.sb.append(this.value);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.value=0;
	}
	
}
