package com.home.commonGame.net.response.login;
import com.home.commonBase.data.login.CreatePlayerData;
import com.home.commonGame.constlist.generate.GameResponseType;
import com.home.commonGame.global.GameC;
import com.home.commonGame.net.response.base.LoginResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 创建角色消息(generated by shine) */
public class CreatePlayerResponse extends LoginResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.CreatePlayer;
	
	/** 数据 */
	public CreatePlayerData data;
	
	public CreatePlayerResponse()
	{
		_dataID=GameResponseType.CreatePlayer;
	}
	
	/** 执行 */
	protected void execute()
	{
		GameC.main.createNewPlayer(gameSocket,data);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "CreatePlayerResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData dataT=stream.readDataFullNotNull();
		if(dataT!=null)
		{
			if(dataT instanceof CreatePlayerData)
			{
				this.data=(CreatePlayerData)dataT;
			}
			else
			{
				this.data=new CreatePlayerData();
				if(!(dataT.getClass().isAssignableFrom(CreatePlayerData.class)))
				{
					stream.throwTypeReadError(CreatePlayerData.class,dataT.getClass());
				}
				this.data.shadowCopy(dataT);
			}
		}
		else
		{
			this.data=null;
		}
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.data=(CreatePlayerData)stream.readDataSimpleNotNull();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("CreatePlayerData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data=null;
	}
	
}
