package com.home.commonGame.net.response.login;
import com.home.commonBase.constlist.generate.InfoCodeType;
import com.home.commonGame.constlist.generate.GameResponseType;
import com.home.commonGame.global.GameC;
import com.home.commonGame.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;

/** 玩家主动下线(generated by shine) */
public class PlayerExitResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.PlayerExit;
	
	public PlayerExitResponse()
	{
		_dataID=GameResponseType.PlayerExit;
		setUseMainThread();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		GameC.main.playerExit(me,InfoCodeType.PlayerExit_initiative);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "PlayerExitResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		stream.endReadObj();
	}
	
}
