package com.home.commonGame.net.response.scene.unit;
import com.home.commonBase.scene.base.Unit;
import com.home.commonGame.constlist.generate.GameResponseType;
import com.home.commonGame.net.response.scene.base.CUnitRResponse;
import com.home.commonGame.net.response.scene.base.SceneRResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 客户端控制单位子弹结束(isClientDrivePosAndAttack下才有意义)(generated by shine) */
public class CUnitRemoveBulletResponse extends CUnitRResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.CUnitRemoveBullet;
	
	/** 子弹实例ID */
	public int bulletInstanceID;
	
	public CUnitRemoveBulletResponse()
	{
		_dataID=GameResponseType.CUnitRemoveBullet;
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		//不给自己广播
		unit.fight.removeBullet(bulletInstanceID,true,false);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "CUnitRemoveBulletResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.bulletInstanceID=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.bulletInstanceID=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("bulletInstanceID");
		writer.sb.append(':');
		writer.sb.append(this.bulletInstanceID);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.bulletInstanceID=0;
	}
	
}
