package com.home.commonGame.net.response.social.chat;
import com.home.commonBase.data.social.chat.ChatData;
import com.home.commonGame.constlist.generate.GameResponseType;
import com.home.commonGame.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 玩家聊天消息(generated by shine) */
public class PlayerChatResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.PlayerChat;
	
	/** 聊天数据 */
	public ChatData data;
	
	/** 频道 */
	public int channel;
	
	/** 二级索引 */
	public long key;
	
	public PlayerChatResponse()
	{
		_dataID=GameResponseType.PlayerChat;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "PlayerChatResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData dataT=stream.readDataFullNotNull();
		if(dataT!=null)
		{
			if(dataT instanceof ChatData)
			{
				this.data=(ChatData)dataT;
			}
			else
			{
				this.data=new ChatData();
				if(!(dataT.getClass().isAssignableFrom(ChatData.class)))
				{
					stream.throwTypeReadError(ChatData.class,dataT.getClass());
				}
				this.data.shadowCopy(dataT);
			}
		}
		else
		{
			this.data=null;
		}
		
		this.channel=stream.readInt();
		
		this.key=stream.readLong();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.data=(ChatData)stream.readDataSimpleNotNull();
		
		this.channel=stream.readInt();
		
		this.key=stream.readLong();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("ChatData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("channel");
		writer.sb.append(':');
		writer.sb.append(this.channel);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("key");
		writer.sb.append(':');
		writer.sb.append(this.key);
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.social.chat(data,channel,key);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data=null;
		this.channel=0;
		this.key=0L;
	}
	
}
