package com.home.commonGame.net.response.social.friend;
import com.home.commonBase.constlist.generate.FunctionType;
import com.home.commonGame.constlist.generate.GameResponseType;
import com.home.commonGame.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 添加好友消息(generated by shine) */
public class AddFriendResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.AddFriend;
	
	/** 角色ID */
	public long playerID;
	
	/** 添加方式(g层定义) */
	public int type;
	
	public AddFriendResponse()
	{
		_dataID=GameResponseType.AddFriend;
		setNeedFunctionID(FunctionType.Friend);
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.friend.addFriend(playerID,type);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "AddFriendResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.playerID=stream.readLong();
		
		this.type=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.playerID=stream.readLong();
		
		this.type=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("type");
		writer.sb.append(':');
		writer.sb.append(this.type);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerID=0L;
		this.type=0;
	}
	
}
