package com.home.commonGame.net.response.system;
import com.home.commonGame.constlist.generate.GameResponseType;
import com.home.commonGame.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 保存bool(generated by shine) */
public class SaveBooleanResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.SaveBoolean;
	
	/** key */
	public int key;
	
	/** 值 */
	public boolean value;
	
	public SaveBooleanResponse()
	{
		_dataID=GameResponseType.SaveBoolean;
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.system.getPartData().keepSave.booleanDic.put(key,value);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SaveBooleanResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.key=stream.readInt();
		
		this.value=stream.readBoolean();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.key=stream.readInt();
		
		this.value=stream.readBoolean();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("key");
		writer.sb.append(':');
		writer.sb.append(this.key);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("value");
		writer.sb.append(':');
		writer.sb.append(this.value);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.key=0;
		this.value=false;
	}
	
}
