package com.home.commonGame.net.response.system;
import com.home.commonGame.constlist.generate.GameResponseType;
import com.home.commonGame.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 保存string消息(generated by shine) */
public class SaveStringResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.SaveString;
	
	/** key */
	public String key="";
	
	/** 值 */
	public String value="";
	
	public SaveStringResponse()
	{
		_dataID=GameResponseType.SaveString;
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.system.getPartData().keepSave.stringDic.put(key,value);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SaveStringResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.key=stream.readUTF();
		
		this.value=stream.readUTF();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.key=stream.readUTF();
		
		this.value=stream.readUTF();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("key");
		writer.sb.append(':');
		writer.sb.append(this.key);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("value");
		writer.sb.append(':');
		writer.sb.append(this.value);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.key="";
		this.value="";
	}
	
}
