package com.home.commonGame.net.response.system;
import com.home.commonBase.data.system.ClientOfflineWorkData;
import com.home.commonGame.constlist.generate.GameResponseType;
import com.home.commonGame.net.base.GameResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送在线的客户端离线事务数据(generated by shine) */
public class SendClientOfflineWorkResponse extends GameResponse
{
	/** 数据类型ID */
	public static final int dataID=GameResponseType.SendClientOfflineWork;
	
	/** 数据 */
	public ClientOfflineWorkData data;
	
	public SendClientOfflineWorkResponse()
	{
		_dataID=GameResponseType.SendClientOfflineWork;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendClientOfflineWorkResponse";
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.system.executeClientOfflineWork(data);
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData dataT=stream.readDataFullNotNull();
		if(dataT!=null)
		{
			if(dataT instanceof ClientOfflineWorkData)
			{
				this.data=(ClientOfflineWorkData)dataT;
			}
			else
			{
				this.data=new ClientOfflineWorkData();
				if(!(dataT.getClass().isAssignableFrom(ClientOfflineWorkData.class)))
				{
					stream.throwTypeReadError(ClientOfflineWorkData.class,dataT.getClass());
				}
				this.data.shadowCopy(dataT);
			}
		}
		else
		{
			this.data=null;
		}
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.data=(ClientOfflineWorkData)stream.readDataSimpleNotNull();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("ClientOfflineWorkData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data=null;
	}
	
}
