package com.home.commonGame.net.serverRequest.center.activity;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.center.base.PlayerToCenterServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 使用激活码(generated by shine) */
public class UseActivationCodeToCenterServerRequest extends PlayerToCenterServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.UseActivationCodeToCenter;
	
	public String code="";
	
	public UseActivationCodeToCenterServerRequest()
	{
		_dataID=ServerMessageType.UseActivationCodeToCenter;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "UseActivationCodeToCenterServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeUTF(this.code);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("code");
		writer.sb.append(':');
		writer.sb.append(this.code);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.code="";
	}
	
	/** 创建实例 */
	public static UseActivationCodeToCenterServerRequest create(long playerID,String code)
	{
		UseActivationCodeToCenterServerRequest re=(UseActivationCodeToCenterServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.code=code;
		return re;
	}
	
}
