package com.home.commonGame.net.serverRequest.center.func.auction;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.item.auction.AuctionBuyItemData;
import com.home.commonBase.data.role.RoleSimpleShowData;
import com.home.commonGame.net.serverRequest.center.func.base.FuncPlayerToCenterServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 拍卖行购买物品到中心服(generated by shine) */
public class FuncSendAuctionBuyItemToCenterServerRequest extends FuncPlayerToCenterServerRequest
{
	/** 玩家数据 */
	public RoleSimpleShowData roleData;
	
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncSendAuctionBuyItemToCenter;
	
	public AuctionBuyItemData data;
	
	public FuncSendAuctionBuyItemToCenterServerRequest()
	{
		_dataID=ServerMessageType.FuncSendAuctionBuyItemToCenter;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		RoleSimpleShowData roleDataTemp=roleData;
		if(roleDataTemp!=null)
		{
			this.roleData=(RoleSimpleShowData)roleDataTemp.clone();
		}
		else
		{
			this.roleData=null;
			nullObjError("roleData");
		}
		
		AuctionBuyItemData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(AuctionBuyItemData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncSendAuctionBuyItemToCenterServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.roleData!=null)
		{
			stream.writeDataSimpleNotNull(this.roleData);
		}
		else
		{
			nullObjError("roleData");
		}
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("roleData");
		writer.sb.append(':');
		if(this.roleData!=null)
		{
			this.roleData.writeDataString(writer);
		}
		else
		{
			writer.sb.append("RoleSimpleShowData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("AuctionBuyItemData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.roleData.release(pool);
		this.roleData=null;
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static FuncSendAuctionBuyItemToCenterServerRequest create(long playerID,int funcID,RoleSimpleShowData roleData,AuctionBuyItemData data)
	{
		FuncSendAuctionBuyItemToCenterServerRequest re=(FuncSendAuctionBuyItemToCenterServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.funcID=funcID;
		re.roleData=roleData;
		re.data=data;
		return re;
	}
	
}
