package com.home.commonGame.net.serverRequest.center.func.auction;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.item.auction.AuctionItemData;
import com.home.commonGame.net.serverRequest.center.base.PlayerToCenterServerRequest;
import com.home.commonGame.net.serverRequest.center.func.base.FuncPlayerToCenterServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送出售物品到中心服消息(generated by shine) */
public class FuncSendAuctionSellItemToCenterServerRequest extends FuncPlayerToCenterServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncSendAuctionSellItemToCenter;
	
	/** 出售物品 */
	public AuctionItemData data;
	
	public FuncSendAuctionSellItemToCenterServerRequest()
	{
		_dataID=ServerMessageType.FuncSendAuctionSellItemToCenter;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		AuctionItemData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(AuctionItemData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncSendAuctionSellItemToCenterServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("AuctionItemData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static FuncSendAuctionSellItemToCenterServerRequest create(long playerID,int funcID,AuctionItemData data)
	{
		FuncSendAuctionSellItemToCenterServerRequest re=(FuncSendAuctionSellItemToCenterServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.funcID=funcID;
		re.data=data;
		return re;
	}
	
}
