package com.home.commonGame.net.serverRequest.center.func.pageShow;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.center.func.base.FuncPlayerToCenterServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送获取翻页显示到中心服(generated by shine) */
public class FuncSendGetPageShowToCenterServerRequest extends FuncPlayerToCenterServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncSendGetPageShowToCenter;
	
	/** 参数(默认0) */
	public int arg;
	
	public int page;
	
	public FuncSendGetPageShowToCenterServerRequest()
	{
		_dataID=ServerMessageType.FuncSendGetPageShowToCenter;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncSendGetPageShowToCenterServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.page);
		
		stream.writeInt(this.arg);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("page");
		writer.sb.append(':');
		writer.sb.append(this.page);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("arg");
		writer.sb.append(':');
		writer.sb.append(this.arg);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.page=0;
		this.arg=0;
	}
	
	/** 创建实例 */
	public static FuncSendGetPageShowToCenterServerRequest create(long playerID,int funcID,int page,int arg)
	{
		FuncSendGetPageShowToCenterServerRequest re=(FuncSendGetPageShowToCenterServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.funcID=funcID;
		re.page=page;
		re.arg=arg;
		return re;
	}
	
}
