package com.home.commonGame.net.serverRequest.center.social;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.social.RoleSocialData;
import com.home.commonGame.net.serverRequest.center.base.PlayerToCenterServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.collection.IntObjectMap;
import com.home.shine.support.pool.DataPool;

/** 提交自定义社交数据到中心服(generated by shine) */
public class CommitCustomRoleSocialToCenterServerRequest extends PlayerToCenterServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.CommitCustomRoleSocialToCenter;
	
	/** 数据组(key:funcID) */
	public IntObjectMap<RoleSocialData> datas;
	
	public CommitCustomRoleSocialToCenterServerRequest()
	{
		_dataID=ServerMessageType.CommitCustomRoleSocialToCenter;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		IntObjectMap<RoleSocialData> datasTemp=datas;
		if(datasTemp!=null)
		{
			this.datas=new IntObjectMap<RoleSocialData>(RoleSocialData[]::new,datasTemp.size());
			IntObjectMap<RoleSocialData> datasT=this.datas;
			if(!datasTemp.isEmpty())
			{
				int datasKFreeValue=datasTemp.getFreeValue();
				int[] datasKKeys=datasTemp.getKeys();
				Object[] datasVValues=datasTemp.getValues();
				for(int datasKI=datasKKeys.length-1;datasKI>=0;--datasKI)
				{
					int datasK=datasKKeys[datasKI];
					if(datasK!=datasKFreeValue)
					{
						RoleSocialData datasV=(RoleSocialData)datasVValues[datasKI];
						int datasW;
						RoleSocialData datasU;
						datasW=datasK;
						
						if(datasV!=null)
						{
							datasU=(RoleSocialData)datasV.clone();
						}
						else
						{
							datasU=null;
							nullObjError("datasU");
						}
						
						datasT.put(datasW,datasU);
					}
				}
			}
		}
		else
		{
			this.datas=null;
			nullObjError("datas");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "CommitCustomRoleSocialToCenterServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.datas!=null)
		{
			stream.writeLen(this.datas.size());
			if(!this.datas.isEmpty())
			{
				int datasKFreeValue=this.datas.getFreeValue();
				int[] datasKKeys=this.datas.getKeys();
				Object[] datasVValues=this.datas.getValues();
				for(int datasKI=datasKKeys.length-1;datasKI>=0;--datasKI)
				{
					int datasK=datasKKeys[datasKI];
					if(datasK!=datasKFreeValue)
					{
						RoleSocialData datasV=(RoleSocialData)datasVValues[datasKI];
						stream.writeInt(datasK);
						
						if(datasV!=null)
						{
							stream.writeDataSimpleNotNull(datasV);
						}
						else
						{
							nullObjError("datasV");
						}
						
					}
				}
			}
		}
		else
		{
			nullObjError("datas");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("datas");
		writer.sb.append(':');
		writer.sb.append("Map<int,RoleSocialData>");
		if(this.datas!=null)
		{
			writer.sb.append('(');
			writer.sb.append(this.datas.size());
			writer.sb.append(')');
			writer.writeEnter();
			writer.writeLeftBrace();
			if(!this.datas.isEmpty())
			{
				int datasKFreeValue=this.datas.getFreeValue();
				int[] datasKKeys=this.datas.getKeys();
				Object[] datasVValues=this.datas.getValues();
				for(int datasKI=datasKKeys.length-1;datasKI>=0;--datasKI)
				{
					int datasK=datasKKeys[datasKI];
					if(datasK!=datasKFreeValue)
					{
						RoleSocialData datasV=(RoleSocialData)datasVValues[datasKI];
						writer.writeTabs();
						writer.sb.append(datasK);
						
						writer.sb.append(':');
						if(datasV!=null)
						{
							datasV.writeDataString(writer);
						}
						else
						{
							writer.sb.append("RoleSocialData=null");
						}
						
						writer.writeEnter();
					}
				}
			}
			writer.writeRightBrace();
		}
		else
		{
			writer.sb.append("=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		if(this.datas!=null)
		{
			if(!this.datas.isEmpty())
			{
				int datasKFreeValue=this.datas.getFreeValue();
				int[] datasKKeys=this.datas.getKeys();
				Object[] datasVValues=this.datas.getValues();
				for(int datasKI=datasKKeys.length-1;datasKI>=0;--datasKI)
				{
					int datasK=datasKKeys[datasKI];
					if(datasK!=datasKFreeValue)
					{
						RoleSocialData datasV=(RoleSocialData)datasVValues[datasKI];
						datasV.release(pool);
						datasV=null;
						datasKKeys[datasKI]=datasKFreeValue;
						datasVValues[datasKI]=null;
					}
				}
			}
			this.datas.justClearSize();
		}
	}
	
	/** 创建实例 */
	public static CommitCustomRoleSocialToCenterServerRequest create(long playerID,IntObjectMap<RoleSocialData> datas)
	{
		CommitCustomRoleSocialToCenterServerRequest re=(CommitCustomRoleSocialToCenterServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.datas=datas;
		return re;
	}
	
}
