package com.home.commonGame.net.serverRequest.center.social;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.social.RoleSocialData;
import com.home.commonGame.net.serverRequest.center.base.PlayerToCenterServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.collection.IntObjectMap;
import com.home.shine.support.collection.IntSet;
import com.home.shine.support.pool.DataPool;

/** 提交角色显示数据到中心服(generated by shine) */
public class CommitRoleSocialToCenterServerRequest extends PlayerToCenterServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.CommitRoleSocialToCenter;
	
	/** 数据 */
	public RoleSocialData data;
	
	public CommitRoleSocialToCenterServerRequest()
	{
		_dataID=ServerMessageType.CommitRoleSocialToCenter;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		RoleSocialData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(RoleSocialData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "CommitRoleSocialToCenterServerRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("RoleSocialData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static CommitRoleSocialToCenterServerRequest create(long playerID,RoleSocialData data)
	{
		CommitRoleSocialToCenterServerRequest re=(CommitRoleSocialToCenterServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.data=data;
		return re;
	}
	
}
