package com.home.commonGame.net.serverRequest.center.system;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.center.base.GameToCenterServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送GM查询结果到中心服(generated by shine) */
public class SendMQueryPlayerWorkResultToCenterServerRequest extends GameToCenterServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.SendMQueryPlayerWorkResultToCenter;
	
	public int httpID;
	
	/** 数据 */
	public BaseData data;
	
	public SendMQueryPlayerWorkResultToCenterServerRequest()
	{
		_dataID=ServerMessageType.SendMQueryPlayerWorkResultToCenter;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		BaseData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(BaseData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendMQueryPlayerWorkResultToCenterServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.httpID);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("httpID");
		writer.sb.append(':');
		writer.sb.append(this.httpID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("BaseData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.httpID=0;
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static SendMQueryPlayerWorkResultToCenterServerRequest create(int httpID,BaseData data)
	{
		SendMQueryPlayerWorkResultToCenterServerRequest re=(SendMQueryPlayerWorkResultToCenterServerRequest)BytesControl.createRequest(dataID);
		re.httpID=httpID;
		re.data=data;
		return re;
	}
	
}
