package com.home.commonGame.net.serverRequest.center.system;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.center.base.GameToCenterServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送在线角色数到中心服消息(generated by shine) */
public class SendPlayerOnlineNumToCenterServerRequest extends GameToCenterServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.SendPlayerOnlineNumToCenter;
	
	public int num;
	
	public SendPlayerOnlineNumToCenterServerRequest()
	{
		_dataID=ServerMessageType.SendPlayerOnlineNumToCenter;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendPlayerOnlineNumToCenterServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.num);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("num");
		writer.sb.append(':');
		writer.sb.append(this.num);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.num=0;
	}
	
	/** 创建实例 */
	public static SendPlayerOnlineNumToCenterServerRequest create(int num)
	{
		SendPlayerOnlineNumToCenterServerRequest re=(SendPlayerOnlineNumToCenterServerRequest)BytesControl.createRequest(dataID);
		re.num=num;
		return re;
	}
	
}
