package com.home.commonGame.net.serverRequest.game.base;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.game.base.GameToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 指定场景消息(generated by shine) */
public class SignedSceneGameToGameServerRequest extends GameToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.SignedSceneGameToGame;
	
	/** 执行器号 */
	public int executorIndex=-1;
	
	/** 实例ID */
	public int instanceID=-1;
	
	public SignedSceneGameToGameServerRequest()
	{
		_dataID=ServerMessageType.SignedSceneGameToGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SignedSceneGameToGameServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.executorIndex);
		
		stream.writeInt(this.instanceID);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("executorIndex");
		writer.sb.append(':');
		writer.sb.append(this.executorIndex);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("instanceID");
		writer.sb.append(':');
		writer.sb.append(this.instanceID);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.executorIndex=0;
		this.instanceID=0;
	}
	
	/** 创建实例 */
	public static SignedSceneGameToGameServerRequest create(int executorIndex,int instanceID)
	{
		SignedSceneGameToGameServerRequest re=(SignedSceneGameToGameServerRequest)BytesControl.createRequest(dataID);
		re.executorIndex=executorIndex;
		re.instanceID=instanceID;
		return re;
	}
	
}
