package com.home.commonGame.net.serverRequest.game.func.pageShow;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.game.func.base.FuncToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 发送获取翻页显示到逻辑服(generated by shine) */
public class FuncSendGetPageShowToGameServerRequest extends FuncToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncSendGetPageShowToGame;
	
	public long playerID;
	
	public int arg;
	
	public int page;
	
	/** 获取rankData的key */
	public long key;
	
	public FuncSendGetPageShowToGameServerRequest()
	{
		_dataID=ServerMessageType.FuncSendGetPageShowToGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncSendGetPageShowToGameServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.playerID);
		
		stream.writeInt(this.page);
		
		stream.writeInt(this.arg);
		
		stream.writeLong(this.key);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("page");
		writer.sb.append(':');
		writer.sb.append(this.page);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("arg");
		writer.sb.append(':');
		writer.sb.append(this.arg);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("key");
		writer.sb.append(':');
		writer.sb.append(this.key);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerID=0L;
		this.page=0;
		this.arg=0;
		this.key=0L;
	}
	
	/** 创建实例 */
	public static FuncSendGetPageShowToGameServerRequest create(int funcID,long playerID,int page,int arg,long key)
	{
		FuncSendGetPageShowToGameServerRequest re=(FuncSendGetPageShowToGameServerRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.playerID=playerID;
		re.page=page;
		re.arg=arg;
		re.key=key;
		return re;
	}
	
}
