package com.home.commonGame.net.serverRequest.game.func.roleGroup;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.game.func.base.FuncToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 移除玩家群简版信息到其他逻辑服(generated by shine) */
public class FuncRemoveRoleGroupSimpleToGameServerRequest extends FuncToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncRemoveRoleGroupSimpleToGame;
	
	public long groupID;
	
	public FuncRemoveRoleGroupSimpleToGameServerRequest()
	{
		_dataID=ServerMessageType.FuncRemoveRoleGroupSimpleToGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncRemoveRoleGroupSimpleToGameServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.groupID);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("groupID");
		writer.sb.append(':');
		writer.sb.append(this.groupID);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.groupID=0L;
	}
	
	/** 创建实例 */
	public static FuncRemoveRoleGroupSimpleToGameServerRequest create(int funcID,long groupID)
	{
		FuncRemoveRoleGroupSimpleToGameServerRequest re=(FuncRemoveRoleGroupSimpleToGameServerRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.groupID=groupID;
		return re;
	}
	
}
