package com.home.commonGame.net.serverRequest.game.func.roleGroup;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.social.roleGroup.RoleGroupChangeData;
import com.home.commonGame.net.serverRequest.game.func.base.FuncPlayerRoleGroupGameToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 改变玩家群显示到每个玩家(generated by shine) */
public class FuncRoleGroupChangeToPlayerServerRequest extends FuncPlayerRoleGroupGameToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncRoleGroupChangeToPlayer;
	
	/** 变更数据 */
	public RoleGroupChangeData changeData;
	
	public FuncRoleGroupChangeToPlayerServerRequest()
	{
		_dataID=ServerMessageType.FuncRoleGroupChangeToPlayer;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		RoleGroupChangeData changeDataTemp=changeData;
		if(changeDataTemp!=null)
		{
			this.changeData=(RoleGroupChangeData)changeDataTemp.clone();
		}
		else
		{
			this.changeData=null;
			nullObjError("changeData");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncRoleGroupChangeToPlayerServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.changeData!=null)
		{
			stream.writeDataSimpleNotNull(this.changeData);
		}
		else
		{
			nullObjError("changeData");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("changeData");
		writer.sb.append(':');
		if(this.changeData!=null)
		{
			this.changeData.writeDataString(writer);
		}
		else
		{
			writer.sb.append("RoleGroupChangeData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.changeData.release(pool);
		this.changeData=null;
	}
	
	/** 创建实例 */
	public static FuncRoleGroupChangeToPlayerServerRequest create(int funcID,long groupID,RoleGroupChangeData changeData)
	{
		FuncRoleGroupChangeToPlayerServerRequest re=(FuncRoleGroupChangeToPlayerServerRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.groupID=groupID;
		re.changeData=changeData;
		return re;
	}
	
}
