import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 回复玩家群进入专属场景参数消息(generated by shine) */
public class FuncRoleGroupReEnterOwnSceneArgToPlayerServerRequest extends FuncPlayerRoleGroupGameToGameServerRequest
{
	/** 场景进入参数 */
	public SceneLocationData location;
	
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncRoleGroupReEnterOwnSceneArgToPlayer;
	
	public FuncRoleGroupReEnterOwnSceneArgToPlayerServerRequest()
	{
		_dataID=ServerMessageType.FuncRoleGroupReEnterOwnSceneArgToPlayer;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		SceneLocationData locationTemp=location;
		if(locationTemp!=null)
		{
			this.location=(SceneLocationData)locationTemp.clone();
		}
		else
		{
			this.location=null;
			nullObjError("location");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncRoleGroupReEnterOwnSceneArgToPlayerServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.location!=null)
		{
			stream.writeDataSimpleNotNull(this.location);
		}
		else
		{
			nullObjError("location");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("location");
		writer.sb.append(':');
		if(this.location!=null)
		{
			this.location.writeDataString(writer);
		}
		else
		{
			writer.sb.append("SceneLocationData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.location.release(pool);
		this.location=null;
	}
	
	/** 创建实例 */
	public static FuncRoleGroupReEnterOwnSceneArgToPlayerServerRequest create(int funcID,long groupID,SceneLocationData location)
	{
		FuncRoleGroupReEnterOwnSceneArgToPlayerServerRequest re=(FuncRoleGroupReEnterOwnSceneArgToPlayerServerRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.groupID=groupID;
		re.location=location;
		return re;
	}
	
}
