package com.home.commonGame.net.serverRequest.game.func.roleGroup;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.game.func.base.FuncPlayerGameToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送加入玩家群类消息结果(generated by shine) */
public class FuncSendRoleGroupJoinResultServerRequest extends FuncPlayerGameToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncSendRoleGroupJoinResult;
	
	public long groupID;
	
	public boolean success;
	
	public FuncSendRoleGroupJoinResultServerRequest()
	{
		_dataID=ServerMessageType.FuncSendRoleGroupJoinResult;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncSendRoleGroupJoinResultServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.groupID);
		
		stream.writeBoolean(this.success);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("groupID");
		writer.sb.append(':');
		writer.sb.append(this.groupID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("success");
		writer.sb.append(':');
		writer.sb.append(this.success);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.groupID=0L;
		this.success=false;
	}
	
	/** 创建实例 */
	public static FuncSendRoleGroupJoinResultServerRequest create(int funcID,long groupID,boolean success)
	{
		FuncSendRoleGroupJoinResultServerRequest re=(FuncSendRoleGroupJoinResultServerRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.groupID=groupID;
		re.success=success;
		return re;
	}
	
}
