package com.home.commonGame.net.serverRequest.game.login;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.game.base.GameToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 回复角色退出返回消息(generated by shine) */
public class PlayerExitSwitchBackServerRequest extends GameToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.PlayerExitSwitchBack;
	
	/** 角色ID */
	public long playerID;
	
	/** 角色列表数据 */
	public BaseData listData;
	
	public PlayerExitSwitchBackServerRequest()
	{
		_dataID=ServerMessageType.PlayerExitSwitchBack;
		setDontCopy();
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		BaseData listDataTemp=listData;
		if(listDataTemp!=null)
		{
			this.listData=(BaseData)listDataTemp.clone();
		}
		else
		{
			this.listData=null;
			nullObjError("listData");
		}
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.playerID);
		
		if(this.listData!=null)
		{
			stream.writeDataSimpleNotNull(this.listData);
		}
		else
		{
			nullObjError("listData");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "PlayerExitSwitchBackServerRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("listData");
		writer.sb.append(':');
		if(this.listData!=null)
		{
			this.listData.writeDataString(writer);
		}
		else
		{
			writer.sb.append("BaseData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerID=0L;
		this.listData=null;
	}
	
	/** 创建实例 */
	public static PlayerExitSwitchBackServerRequest create(long playerID,BaseData listData)
	{
		PlayerExitSwitchBackServerRequest re=(PlayerExitSwitchBackServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.listData=listData;
		return re;
	}
	
}
