package com.home.commonGame.net.serverRequest.game.login;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.login.PlayerLoginToEachGameData;
import com.home.commonGame.net.serverRequest.game.base.GameToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 登陆拉取数据消息(generated by shine) */
public class PlayerLoginToEachGameServerRequest extends GameToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.PlayerLoginToEachGame;
	
	/** 角色id */
	public long playerID;
	
	/** 登陆拉取数据 */
	public PlayerLoginToEachGameData data;
	
	public PlayerLoginToEachGameServerRequest()
	{
		_dataID=ServerMessageType.PlayerLoginToEachGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		PlayerLoginToEachGameData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(PlayerLoginToEachGameData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "PlayerLoginToEachGameServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.playerID);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("PlayerLoginToEachGameData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerID=0L;
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static PlayerLoginToEachGameServerRequest create(long playerID,PlayerLoginToEachGameData data)
	{
		PlayerLoginToEachGameServerRequest re=(PlayerLoginToEachGameServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.data=data;
		return re;
	}
	
}
