package com.home.commonGame.net.serverRequest.game.login;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.login.RePlayerLoginFromEachGameData;
import com.home.commonGame.net.serverRequest.game.base.GameToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 回复登陆逻辑服角色(generated by shine) */
public class RePlayerLoginFromEachGameServerRequest extends GameToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.RePlayerLoginFromEachGame;
	
	public long playerID;
	
	public RePlayerLoginFromEachGameData data;
	
	public RePlayerLoginFromEachGameServerRequest()
	{
		_dataID=ServerMessageType.RePlayerLoginFromEachGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		RePlayerLoginFromEachGameData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(RePlayerLoginFromEachGameData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "RePlayerLoginFromEachGameServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.playerID);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("RePlayerLoginFromEachGameData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerID=0L;
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static RePlayerLoginFromEachGameServerRequest create(long playerID,RePlayerLoginFromEachGameData data)
	{
		RePlayerLoginFromEachGameServerRequest re=(RePlayerLoginFromEachGameServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.data=data;
		return re;
	}
	
}
