package com.home.commonGame.net.serverRequest.game.login;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.game.base.GameToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 回复角色预切换game消息(generated by shine) */
public class RePlayerPreSwitchGameToGameServerRequest extends GameToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.RePlayerPreSwitchGameToGame;
	
	/** 是否需要退出 */
	public boolean needExit;
	
	/** 角色ID */
	public long playerID;
	
	public RePlayerPreSwitchGameToGameServerRequest()
	{
		_dataID=ServerMessageType.RePlayerPreSwitchGameToGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "RePlayerPreSwitchGameToGameServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.playerID);
		
		stream.writeBoolean(this.needExit);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("needExit");
		writer.sb.append(':');
		writer.sb.append(this.needExit);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerID=0L;
		this.needExit=false;
	}
	
	/** 创建实例 */
	public static RePlayerPreSwitchGameToGameServerRequest create(long playerID,boolean needExit)
	{
		RePlayerPreSwitchGameToGameServerRequest re=(RePlayerPreSwitchGameToGameServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.needExit=needExit;
		return re;
	}
	
}
