package com.home.commonGame.net.serverRequest.game.login;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.game.base.GameToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 回复游戏服角色切换回消息(generated by shine) */
public class RePreSwitchGameServerRequest extends GameToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.RePreSwitchGame;
	
	/** 角色ID */
	public long playerID;
	
	/** 令牌 */
	public int token;
	
	/** 游戏服地址(因在每个game存储所有game服的地址端口成本更高,所以用每次推送实现) */
	public String host="";
	
	/** 游戏服端口 */
	public int port;
	
	public RePreSwitchGameServerRequest()
	{
		_dataID=ServerMessageType.RePreSwitchGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.playerID);
		
		stream.writeInt(this.token);
		
		stream.writeUTF(this.host);
		
		stream.writeInt(this.port);
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "RePreSwitchGameServerRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("token");
		writer.sb.append(':');
		writer.sb.append(this.token);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("host");
		writer.sb.append(':');
		writer.sb.append(this.host);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("port");
		writer.sb.append(':');
		writer.sb.append(this.port);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerID=0L;
		this.token=0;
		this.host="";
		this.port=0;
	}
	
	/** 创建实例 */
	public static RePreSwitchGameServerRequest create(long playerID,int token,String host,int port)
	{
		RePreSwitchGameServerRequest re=(RePreSwitchGameServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.token=token;
		re.host=host;
		re.port=port;
		return re;
	}
	
}
