package com.home.commonGame.net.serverRequest.game.login;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.game.base.GameToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 更新逻辑服登录限制消息(generated by shine) */
public class RefreshGameLoginLimitToGameServerRequest extends GameToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.RefreshGameLoginLimitToGame;
	
	public boolean isLoginLimit;
	
	public RefreshGameLoginLimitToGameServerRequest()
	{
		_dataID=ServerMessageType.RefreshGameLoginLimitToGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "RefreshGameLoginLimitToGameServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeBoolean(this.isLoginLimit);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("isLoginLimit");
		writer.sb.append(':');
		writer.sb.append(this.isLoginLimit);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.isLoginLimit=false;
	}
	
	/** 创建实例 */
	public static RefreshGameLoginLimitToGameServerRequest create(boolean isLoginLimit)
	{
		RefreshGameLoginLimitToGameServerRequest re=(RefreshGameLoginLimitToGameServerRequest)BytesControl.createRequest(dataID);
		re.isLoginLimit=isLoginLimit;
		return re;
	}
	
}
