package com.home.commonGame.net.serverRequest.game.social;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.role.RoleShowChangeData;
import com.home.commonGame.net.serverRequest.game.base.GameToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 更新角色显示数据到源服(generated by shine) */
public class RefreshRoleShowToSourceGameServerRequest extends GameToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.RefreshRoleShowToSourceGame;
	
	/** 角色ID */
	public long playerID;
	
	/** 改变数据 */
	public RoleShowChangeData data;
	
	public RefreshRoleShowToSourceGameServerRequest()
	{
		_dataID=ServerMessageType.RefreshRoleShowToSourceGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		RoleShowChangeData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(RoleShowChangeData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.playerID);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "RefreshRoleShowToSourceGameServerRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("RoleShowChangeData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerID=0L;
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static RefreshRoleShowToSourceGameServerRequest create(long playerID,RoleShowChangeData data)
	{
		RefreshRoleShowToSourceGameServerRequest re=(RefreshRoleShowToSourceGameServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.data=data;
		return re;
	}
	
}
