package com.home.commonGame.net.serverRequest.game.system;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.login.GameLoginToGameData;
import com.home.commonGame.net.serverRequest.game.base.GameToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 逻辑服确认消息(generated by shine) */
public class BeGameToGameServerRequest extends GameToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.BeGameToGame;
	
	/** 数据 */
	public GameLoginToGameData data;
	
	public int id;
	
	public BeGameToGameServerRequest()
	{
		_dataID=ServerMessageType.BeGameToGame;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		GameLoginToGameData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(GameLoginToGameData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "BeGameToGameServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("id");
		writer.sb.append(':');
		writer.sb.append(this.id);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("GameLoginToGameData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.id=0;
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static BeGameToGameServerRequest create(int id,GameLoginToGameData data)
	{
		BeGameToGameServerRequest re=(BeGameToGameServerRequest)BytesControl.createRequest(dataID);
		re.id=id;
		re.data=data;
		return re;
	}
	
}
