package com.home.commonGame.net.serverRequest.game.system;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.system.PlayerWorkData;
import com.home.commonGame.net.serverRequest.game.base.GameToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送角色事务到逻辑服(generated by shine) */
public class SendPlayerWorkToGameServerRequest extends GameToGameServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.SendPlayerWorkToGame;
	
	/** 事务 */
	public PlayerWorkData data;
	
	public SendPlayerWorkToGameServerRequest()
	{
		_dataID=ServerMessageType.SendPlayerWorkToGame;
		setDontCopy();
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		PlayerWorkData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(PlayerWorkData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendPlayerWorkToGameServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("PlayerWorkData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data=null;
	}
	
	/** 创建实例 */
	public static SendPlayerWorkToGameServerRequest create(PlayerWorkData data)
	{
		SendPlayerWorkToGameServerRequest re=(SendPlayerWorkToGameServerRequest)BytesControl.createRequest(dataID);
		re.data=data;
		return re;
	}
	
}
