package com.home.commonGame.net.serverRequest.login.login;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.login.base.GameToLoginServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 客户端申请绑定平台消息(generated by shine) */
public class ClientApplyBindPlatformToLoginServerRequest extends GameToLoginServerRequest
{
	/** 发起玩家id */
	public long playerID;
	
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.ClientApplyBindPlatformToLogin;
	
	public long userID;
	
	public String uid="";
	
	public String platform="";
	
	public ClientApplyBindPlatformToLoginServerRequest()
	{
		_dataID=ServerMessageType.ClientApplyBindPlatformToLogin;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ClientApplyBindPlatformToLoginServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.playerID);
		
		stream.writeLong(this.userID);
		
		stream.writeUTF(this.uid);
		
		stream.writeUTF(this.platform);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("userID");
		writer.sb.append(':');
		writer.sb.append(this.userID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("uid");
		writer.sb.append(':');
		writer.sb.append(this.uid);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("platform");
		writer.sb.append(':');
		writer.sb.append(this.platform);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerID=0L;
		this.userID=0L;
		this.uid="";
		this.platform="";
	}
	
	/** 创建实例 */
	public static ClientApplyBindPlatformToLoginServerRequest create(long playerID,long userID,String uid,String platform)
	{
		ClientApplyBindPlatformToLoginServerRequest re=(ClientApplyBindPlatformToLoginServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.userID=userID;
		re.uid=uid;
		re.platform=platform;
		return re;
	}
	
}
