package com.home.commonGame.net.serverRequest.login.system;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.login.base.GameToLoginServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 限制某取服注册到登陆服(generated by shine) */
public class LimitAreaToLoginServerRequest extends GameToLoginServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.LimitAreaToLogin;
	
	/** 是否限制注册 */
	public boolean isLimitRegist;
	
	/** 区服ID */
	public int areaID;
	
	public LimitAreaToLoginServerRequest()
	{
		_dataID=ServerMessageType.LimitAreaToLogin;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "LimitAreaToLoginServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.areaID);
		
		stream.writeBoolean(this.isLimitRegist);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("areaID");
		writer.sb.append(':');
		writer.sb.append(this.areaID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("isLimitRegist");
		writer.sb.append(':');
		writer.sb.append(this.isLimitRegist);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.areaID=0;
		this.isLimitRegist=false;
	}
	
	/** 创建实例 */
	public static LimitAreaToLoginServerRequest create(int areaID,boolean isLimitRegist)
	{
		LimitAreaToLoginServerRequest re=(LimitAreaToLoginServerRequest)BytesControl.createRequest(dataID);
		re.areaID=areaID;
		re.isLimitRegist=isLimitRegist;
		return re;
	}
	
}
