package com.home.commonGame.net.serverRequest.login.system;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.system.UserWorkData;
import com.home.commonGame.net.serverRequest.login.base.GameToLoginServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送user事务到登陆服消息(generated by shine) */
public class SendUserWorkToLoginServerRequest extends GameToLoginServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.SendUserWorkToLogin;
	
	public UserWorkData data;
	
	public SendUserWorkToLoginServerRequest()
	{
		_dataID=ServerMessageType.SendUserWorkToLogin;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		UserWorkData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(UserWorkData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendUserWorkToLoginServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("UserWorkData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static SendUserWorkToLoginServerRequest create(UserWorkData data)
	{
		SendUserWorkToLoginServerRequest re=(SendUserWorkToLoginServerRequest)BytesControl.createRequest(dataID);
		re.data=data;
		return re;
	}
	
}
